<template>
  <div class="cps">
    <a-row type="flex" justify="start" align="middle" :gutter="[0, 1]">
      <a-col :xs="{ span: 10,offset1 }" :sm="16" :md="12" :lg="8" :xl="{ span: 2, offset: 8 }">
        <a-image
          :width="110"
          :src="bookInfo['Img']"
          :preview="false"
          fallback="data:image/png;base64,"
        />
      </a-col>
      <a-col :xs="{ span: 12, }" :sm="16" :md="12" :lg="8" :xl="{ span: 8, push: 1 }" class="info">
        <a-row>
          <a-col>
            <a-space direction="vertical " size="small">
              <a-space>
                <h2>{{ bookInfo['Name'] }}</h2>
                <span>{{ bookInfo['Author'] }} 著</span>
              </a-space>

              <a-space>
                <span>{{ bookInfo['CName'] }}</span>
                <span>|</span>
                <span>{{ bookInfo['BookStatus'] }}</span>
              </a-space>
              <a-space align="center">
                <span>评分:</span>
                <a-rate :value="bookInfo['Rate'] / 2" />
              </a-space>
              <a-space>
                <a-button @click="goContent(0)">免费试读</a-button>
                <a-button @click="joinShelf" :loading="loading">{{ inS ? '移出书架' : '加入书架' }}</a-button>
              </a-space>
            </a-space>
          </a-col>
        </a-row>
      </a-col>
    </a-row>
    <a-tabs>
      <a-tab-pane key="1" tab="作品信息">
        <span>{{ bookInfo['Desc'] }}</span>
        <a-divider />
        <span>
          <a-space size="large">
            <span style="color: #999;">最新章节</span>
            <a @click="goContent(chaptersLen - 1)">{{ bookInfo['LastChapter'] }}</a>
            <span style="color: #999;">{{ bookInfo['LastTime'] }}</span>
          </a-space>
        </span>
        <a-divider />
        <a-row type="flex" justify="center">
          <a-col
            :xs="12"
            :sm="16"
            :md="12"
            :lg="8"
            :xl="4"
            v-for="(item, index) in bookInfo['SameAuthorBooks']"
            :key="item.Id"
            class="height-120"
            @click="goChapters(item.Id)"
          >
            <div>
              <center>
                <a-image
                  :width="100"
                  :src="item.Img"
                  :preview="false"
                  fallback="data:image/png;base64,"
                />
                <p class="author">{{ item.Name }}</p>
              </center>
            </div>
          </a-col>
        </a-row>
      </a-tab-pane>
      <a-tab-pane key="2" :tab="'目录(' + chaptersLen + '章)'" force-render>
        <a-row type="flex" justify="space-around  " align="middle" :gutter="[0, 10]">
          <a-col
            :xs="20"
            :sm="16"
            :md="12"
            :lg="8"
            :xl="{ span: 6, offset: 2 }"
            v-for="(item, index) in chapters"
            :key="index"
          >
            <a @click="goContent(index)" class="item">{{ item.name }}</a>
            <!--                        <a-divider />-->
          </a-col>
        </a-row>
        <a-back-top />
      </a-tab-pane>
    </a-tabs>
  </div>
</template>

<script>
import http from "../axios";

export default {
  name: "Chapters",
  data() {
    return {
      chapters: [],
      bookInfo: {},
      sk: 1,
      inS: false,
      loading: false,
      bid: this.$route.params.bid
    }
  },
  created() {
    this.fetchBookInfo()
    this.fetchChapters()
    this.inShelf()
  },
  computed: {
    chaptersLen() {
      return this.$store.state.chapters.length;
    },

  },
  watch: {
    bid() {
      this.fetchBookInfo()
      this.fetchChapters()
      this.inShelf()
    }
  },

  methods: {
    async fetchBookInfo() {

      const response = await http.get("/book/detail/" + this.$store.state.bid);
      this.bookInfo = response.data
      this.$store.commit("set_book_info", this.bookInfo)

    },
    async fetchChapters() {

      const response = await http.get("/book/chapters/" + this.$store.state.bid + "/0");
      this.chapters = response.data;
      this.$store.commit("set_chapters", this.chapters)


    },
    goContent(item) {
      // this.$store.commit("set_chapterIdx", item)

      this.$router.push({
        name:"content",
        params:{
          idx:item
        }
      })
    },
    async joinShelf() {
      this.loading = true
      const action = this.inS ? "del" : "add"
      const response = await http.get("/book/action/" + this.$store.state.bid + "/" + action);
      if (response['code'] == 200) {
        this.inS = !this.inS
      }
      this.loading = false
    },
    async getShelf() {
      const response = await http.get("/book/shelf");
      this.shelf = response.data;
      this.$store.commit("set_shelf", this.shelf);
    },
    inShelf() {
      let inB = false;
      const shelf = this.$store.state.shelf
      for (let i = 0; i < shelf.length; i++) {
        if (shelf[i]['Id'] === this.$store.state.bid) {
          inB = true
          break
        }
      }
      this.inS = inB;
    },
    goChapters(bid) {
      this.$store.commit("set_bid", bid);
      this.bid = bid
    }
  },
}
</script>

