<template>
    <a-row type="flex" justify="center" :gutter="[5, 5]" class="searchInput">
        <a-col :xs="24" :sm="16" :md="12" :lg="8" :xl="{ span: 14, }">
            <a-input-search
                v-model:value="value"
                placeholder="搜索书名/作者"
                enter-button
                @search="onSearch"
            />
        </a-col>
        <a-col :span="24"></a-col>
        <a-col
            :xs="8"
            :sm="2"
            :md="2"
            :lg="2"
            :xl="{ span: 2, }"
            v-for="(item, index) in cates"
            :key="index"
            style="text-align: center;"
        >
            <a @click="fetchCateBooks(item)">{{ item }}</a>
        </a-col>
    </a-row>

    <div class="bks">
        <a-row type="flex" justify="start">
            <a-col
                :xs="12"
                :sm="16"
                :md="12"
                :lg="8"
                :xl="4"
                v-for="(item, index) in books"
                :key="item.Id"
                class="height-120"
                @click="chapters(item.Id)"
            >
                <div class="bks">
                    <a-image
                        :width="100"
                        :src="item.Img"
                        :preview="false"
                        fallback="data:image/png;base64,"
                    />
                    <p class="author">
                        {{ item.Name }}|
                        <em>{{ item.Author }}</em>
                    </p>
                </div>
            </a-col>
        </a-row>
        <a-pagination
            v-show="books.length > 0"
            :total="500"
            v-model:current="page"
            v-model:pageSize="size"
            @change="onSearch"
        >
            <template #itemRender="{ page, type, originalElement }">
                <a v-if="type === 'prev'">上一页</a>
                <a v-else-if="type === 'next'">下一页</a>
                <renderVNode v-else :vnode="originalElement"></renderVNode>
            </template>
        </a-pagination>
    </div>
</template>

<script>import { ref } from 'vue';
import http from "../axios";
function renderVNode(_, { attrs: { vnode } }) {
    return vnode;
}
export default {
    name: "Index",
    components: {
        renderVNode,
    },
    setup(props) {
        const value = ref('');

        const onSearch = searchValue => {

        };

        return {
            value,
            onSearch,
        };
    },
    data() {
        return {
            cates: [],
            cate: '',
            page: 1,
            size: 54,
            books: []
        }
    },
    created() {
        this.initCategorys()
    },
    watch: {

    },
    methods: {
        async initCategorys() {
            var res = await http.get("/book/category");
            this.cates = res.data.filter(item => item != "")
        },
        async fetchCateBooks(cate) {
            console.log(cate)
            var res = await http.get("/book/category/" + cate + "/" + this.page + "/" + this.size);
            this.books = res.data
            console.log(this.books)
        },
        async onSearch() {
            var res = await http.get("/book/search", {
                params: {
                    "key": this.value,
                    "page": this.page,
                    "size": this.size
                }
            });
            this.books = res.data

        },

        chapters(bid) {
            this.$store.commit("set_bid", bid);
            this.$router.push("chapters");
        }
    },
}
</script>

<style>
.bks {
    margin-top: 20px;
    text-align: center;
}
</style>